$(document).ready(function(){
	var input=[];
	$("#frm_register_student").submit(function(e){
		e.preventDefault();
		var user_password=$("#user_password").val();
		var confirm_pwd=$("#confirm_pwd").val();
		if(user_password.length>=6){
			if(user_password.match(confirm_pwd)){
				registerStudent(new FormData(this));
			}else{
				showRegResult("Password do not match.Check again Please");
			}
		}else{
			showRegResult("Password Must be atleast 6 characters")
		}
	});
	$("#frm_user_login").submit(function(e){
		e.preventDefault();
		loginUser(new FormData(this));
	});
	$("#frm_reg_instructor").submit(function(e){
		e.preventDefault();
		var password=$("#t_password").val();
		var confirm_pwd=$("#confirm_pwd").val();
		if(password.length>=6){
			if(password.match(confirm_pwd)){
				registerTeacher(new FormData(this));
			}else{
				showRegResult("Password do not match.Check again Please");
			}
		}else{
			showRegResult("Password Must be atleast 6 characters");
		}
	});
	$("#form_edit_bio").submit(function(e){
		e.preventDefault();
		var full_names=$("#full_names").val();
		var profession=$("#profession").val();
		var email=$("#email").val();
		var phone=$("#phone").val();
		var address=$("#address").val();
		alert(full_names);
	});
	$("#profile_image_input").on("change",function(){
		var file=document.getElementById("profile_image_input").files[0];
		var name = document.getElementById("profile_image_input").files[0].name;
		var ext = name.split('.').pop().toLowerCase();
		  if(jQuery.inArray(ext, ['gif','png','jpg','jpeg']) == -1) 
			  {
		      $("#profile_image_input").val("");
			   alert("Invalid Image File");
			  }else{
				var reader = new FileReader();
				reader.onload = function(){
					var dataURL = reader.result;
					var output = document.getElementById('profile');
					output.src = dataURL;
				};
				reader.readAsDataURL(file);
				//upload image now
				var form_data = new FormData();
				form_data.append("file", document.getElementById('profile_image_input').files[0]);
				updateProfileImage(form_data);
			  }

	});
	$("a#btn_request_course").click(function(){
		var course_id=$(this).attr("course");
		var student=$(this).attr("student");
		if(confirm("Do you want to request This Course")){
			input[0]="request_course";
			input[1]=course_id;
			input[2]=student;
			serverRequest(input,false,"",false,"");
		}
	});
	$("#frm_contact_us").submit(function(e){
		e.preventDefault();
		var names=$("#names").val();
		var email=$("#email").val();
		var phone=$("#phone").val();
		var message=$("#message").val();

		input[0]="contact_us";
		input[1]=names;
		input[2]=email;
		input[3]=phone;
		input[4]=message;
		contactUs(input,false,"",false,"");
	});
	$("#frm_change_pwd").submit(function(e){
		e.preventDefault();
		var current_pwd=$("#current_pwd").val();
		var new_pwd=$("#new_pwd").val();
		var confirm_pwd=$("#confirm_pwd").val();
		if(new_pwd.length>=6){
			if(new_pwd.match(confirm_pwd)){
				input[0]="change_password";
				input[1]=current_pwd;
				input[2]=new_pwd;
				input[3]=confirm_pwd;
				input[4]=logged_in;
				serverRequest(input,false,'',false,'');
			}else{
				alert("New Password do not match its confirmation!!Check again");
			}
		}else{
			alert("New password must be atleast 6 characters");
		}
	});
	$("#frm_change_profile").submit(function(e){
		e.preventDefault();
		var user_names=$("#user_names").val();
		var user_email=$("#user_email").val();
		var user_phone=$("#user_phone").val();
		var user_address=$("#user_address").val();

		input[0]="changes_user_profile";
		input[1]=user_names;
		input[2]=user_email;
		input[3]=user_phone;
		input[4]=user_address;
		input[5]=logged_in;
		serverRequest(input,false,'',false,'');
	});
	$("ul.questions_list>li>input").click(function(){
		alert();
	});
});
function showLoader(loaderRef){
	loaderRef.show();
}
function hideLoader(loaderRef){
	loaderRef.hide();
}
function showRegResult(msg){
	var div_result=$("#div_result");
	var success_msg="You have Successfully being registered.Please Login to your Account";
	if(msg.match("200")){
		div_result.show().html(success_msg);
		div_result.css({
			background:'#009966',
			color:'#fff'
		});
	}else{
		div_result.show().html(msg);
		div_result.css({
			background:'#B6151B',
			color:'#fff'
		});
	}
}
function registerTeacher(form_data){	
	var img_loader=$("#img_loader");
	showLoader(img_loader);
	$.ajax({  
	    url:"register_teacher",  
	    method:"POST",  
	    data:form_data,  
	    contentType:false,  
	    processData:false,  
	    success:function(data)  
	    { 
	    	hideLoader(img_loader);
	    	showRegResult(data);        
	    }  
	});
}
function updateProfileImage(form_data){
   $.ajax({
    url:"upload_pic",
    method:"POST",
    data: form_data,
    contentType: false,
    cache: false,
    processData: false,
    beforeSend:function(){
    },   
    success:function(data)
    {
     if(data.match("200")){
     	location.reload();
     }else{
     	alert(data);
     }
    }
   });
}
function registerStudent(form_data){	
	var img_loader=$("#img_loader");
	showLoader(img_loader);
	$.ajax({  
	    url:"register_student",  
	    method:"POST",  
	    data:form_data,  
	    contentType:false,  
	    processData:false,  
	    success:function(data)  
	    { 
	    	hideLoader(img_loader);
	    	showRegResult(data);        
	    }  
	});
}
function loginUser(form_data){
	var img_loader=$("#img_loader");
	var redirectUrl="student";
	showLoader(img_loader);
	$.ajax({  
	    url:"login_user",  
	    method:"POST",  
	    data:form_data,  
	    contentType:false,  
	    processData:false,  
	    success:function(data)  
	    { 
	    	hideLoader(img_loader);
	    	if(data.match("200")){
	    		window.location=redirectUrl;
	    	}else{
	    		showRegResult(data); 
	    	}
	    	       
	    }  
	});
}

function serverRequest(input,hasLoader,loaderReference,has_redirect,redirectUrl){
	var url="request_handler";
	if(hasLoader==true){
		showLoader(loaderReference);
	}
	$.post(url,{
		input:input
	},function(response){
		if(hasLoader){
			hideLoader(loaderReference);
		}
		if(response.match("200")){
			if(has_redirect){
				window.location=redirectUrl;
			}else{
				location.reload();
			}
			
		}else{
			alert(response);
		}
		
	});
}
function ServerResponse(input,hasLoader,loaderReference,has_redirect,outputPlace){
	var url="request_handler";
	if(hasLoader==true){
		showLoader(loaderReference);
	}
	$.post(url,{
		input:input
	},function(response){
		alert(response);
		outputPlace.show().html(response);
	});
}
function contactUs(input,hasLoader,loaderReference,has_redirect,redirectUrl){
	var url="request_handler";
	if(hasLoader==true){
		showLoader(loaderReference);
	}
	$.post(url,{
		input:input
	},function(response){
		alert(response);
		$("#para_responspe").show().html(response);
	});
}
